mapper.select:
        select
ciId, ciName, ciContent, meaning, remarks
        from check_item


                and ciId = #{ciId}


                and ciName = #{ciName}


                and ciContent = #{ciContent}


                and meaning = #{meaning}


                and remarks = #{remarks}

select count(*)
from check_item


        and ciId = #{ciId}


        and ciName = #{ciName}


        and ciContent = #{ciContent}


        and meaning = #{meaning}


        and remarks = #{remarks}
