mapper.select:
        select
cidrId, name, unit, minrange, maxrange, normalValue, normalValueString, type, value, isError, ciId, orderId
        from cidetailed_report


                and cidrId = #{cidrId}


                and name = #{name}


                and unit = #{unit}


                and minrange = #{minrange}


                and maxrange = #{maxrange}


                and normalValue = #{normalValue}


                and normalValueString = #{normalValueString}


                and type = #{type}


                and value = #{value}


                and isError = #{isError}


                and ciId = #{ciId}


                and orderId = #{orderId}

select count(*)
from cidetailed_report


        and cidrId = #{cidrId}


        and name = #{name}


        and unit = #{unit}


        and minrange = #{minrange}


        and maxrange = #{maxrange}


        and normalValue = #{normalValue}


        and normalValueString = #{normalValueString}


        and type = #{type}


        and value = #{value}


        and isError = #{isError}


        and ciId = #{ciId}


        and orderId = #{orderId}
